--
-- Table structure for table `invoiceitems`
--

DROP TABLE IF EXISTS `invoiceitems`;
CREATE TABLE IF NOT EXISTS `invoiceitems` (
  `IiId` int(11) NOT NULL AUTO_INCREMENT,
  `IiInId` int(11) NOT NULL,
  `IiOiPartNo` varchar(15) NOT NULL,
  `IiOiSupplierNo` varchar(15) DEFAULT NULL,
  `IiOiDescription` varchar(100) DEFAULT NULL,
  `IiOiLeftQty` int(10) UNSIGNED NOT NULL,
  `IiOiRightQty` int(10) UNSIGNED NOT NULL,
  `IiOiTotalQty` int(11) NOT NULL,
  `IiOiPrice` decimal(8,2) NOT NULL,
  `IiOiAmount` decimal(10,2) NOT NULL,
  `IiOiCreatedOn` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `OiCreatedOn` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`IiId`),
  KEY `IiInId` (`IiInId`)
) ENGINE=InnoDB AUTO_INCREMENT=259 DEFAULT CHARSET=utf8;

