--
-- Triggers `invoiceitems`
--
DROP TRIGGER IF EXISTS `update_invmaster_amounts_after_insert`;
DELIMITER $$
CREATE TRIGGER `update_invmaster_amounts_after_insert` AFTER INSERT ON `invoiceitems` FOR EACH ROW BEGIN	
    DECLARE vat_percent DECIMAL(5,2);
    SET vat_percent = (select InVatPercent from invoicemaster where InId = NEW.IiInId);
	UPDATE `invoicemaster` `im`
    INNER JOIN (select IiInId, sum(IiOiAmount) as 'Amount', (sum(IiOiAmount)*vat_percent)/100 as 'vatamount' from invoiceitems group by IiInId) `ii` on `im`.`InId` = `ii`.`IiInId` SET `im`.`inAmount` = `ii`.`Amount`, `im`.`inVatAmount` = `ii`.`vatamount`;
    END
$$
DELIMITER ;
