--
-- Table structure for table `invoicemaster`
--

DROP TABLE IF EXISTS `invoicemaster`;
CREATE TABLE IF NOT EXISTS `invoicemaster` (
  `InId` int(11) NOT NULL AUTO_INCREMENT,
  `InOmId` int(11) NOT NULL,
  `InOmCompanyCode` varchar(6) NOT NULL,
  `InOmCompanyName` varchar(150) NOT NULL,
  `InOmCreatedOn` date NOT NULL,
  `InOmLpo` varchar(100) NOT NULL,
  `inDueDate` date NOT NULL,
  `InAmount` decimal(10,2) NOT NULL DEFAULT '0.00',
  `InDiscount` decimal(10,2) NOT NULL DEFAULT '0.00',
  `InVatPercent` decimal(5,2) NOT NULL,
  `InVatAmount` decimal(10,2) NOT NULL DEFAULT '0.00',
  `InNetAmount` decimal(11,2) NOT NULL DEFAULT '0.00',
  `InOmAdd` varchar(200) NOT NULL,
  `InOmTel1` varchar(50) NOT NULL,
  `InOmTel2` varchar(50) NOT NULL,
  `InCreatedBy` int(11) NOT NULL COMMENT 'This is User ID who made Invoice',
  `InCreatedOn` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'This is created timestamp for invoice creation',
  `InViewCount` mediumint(8) UNSIGNED NOT NULL,
  PRIMARY KEY (`InId`),
  UNIQUE KEY `InOmId` (`InOmId`),
  KEY `InCreatedBy` (`InCreatedBy`)
) ENGINE=InnoDB AUTO_INCREMENT=115 DEFAULT CHARSET=utf8;

--
-- Dumping data for table `invoicemaster`
--


--
-- Triggers `invoicemaster`
--
DROP TRIGGER IF EXISTS `netAmount_before_update`;
DELIMITER $$
CREATE TRIGGER `netAmount_before_update` BEFORE UPDATE ON `invoicemaster` FOR EACH ROW BEGIN 
	SET NEW.InNetAmount = (NEW.InAmount - NEW.InDiscount) + NEW.InVatAmount;
END
$$
DELIMITER ;
